var SchemaFilter={schemaClass:new Set(),remove:function(type='micro'){console.log(type);switch(type){case 'micro':this.removeMicroData();console.log("Removing Micro");break;case 'json-ld':this.removeJSONLD();console.log("Removing JSON-LD");break;case 'rdfa':this.removeMicroData();break}},removeMicroData:function(){var typeElements=document.querySelectorAll('[itemtype]');if(typeElements.length===0){typeElements=document.querySelector("html");}var filteredElements=[];if(this.schemaClass.length!==0){for(var i=0;i<typeElements.length;i+=1){var ele=typeElements[i];var type=ele.getAttribute('itemtype');var normalizedType=type.split('/').pop();if(this.schemaClass.has(normalizedType)){filterElements.push(ele)}}}else{filteredElements=typeElements}for(var i=0;i<filteredElements.length;i+=1){var element=filteredElements[i];var taggedChildren=element.querySelectorAll('itemprop');for(var j=0;j<taggedChildren.length;j+=1){var childElement=taggedChildren[i];childElement.removeAttribute('itemprop');childElement.removeAttribute('content')}}},removeJSONLD:function(){var scriptElements=document.querySelectorAll('[type="application/ld+json"]');var jsonLDElements=[];for(var i=0;i<scriptElements.length;i+=1){var obj=JSON.parse(scriptElements[i].innerHTML);if(this.schemaClass===''){scriptElements[i].remove();continue}jsonLDElements.push([scriptElements[i],obj])}for(var i=0;i<jsonLDElements.length;i+=1){var element=jsonLDElements[i];var script=element[0];var jObj=element[1];if(this.schemaClass.has(jObj['@type'])){script.remove()}}},removeRDFa:function(){var markedTags=document.querySelectorAll('[typeof]');var tagsToRemove=[];if(this.schemaClass.length!==0){for(var i=0;i<markedTags.length;i+=1){var ele=markedTags[i];if(this.schemaClass.has(ele.getAttribute('typeof'))){tagsToRemove.push(ele)}}}else{tagsToRemove=markedTags}tagsToRemove.forEach(function(ele){var elements=ele.querySelectorAll('[property]');for(var i=0;i<elements.length;i+=1){elements[i].removeAttribute('property');elements[i].removeAttribute('typeof')}})}};
